
struct Solution {
    // ... fields
}

struct Child {
    left: Option<Box<Child>>,
    right: Option<Box<Child>>,
    index: usize,
    value: i32
}

struct Data {
    index: usize,
    value: i32
}



impl Solution {

    // tree[]
    // tree_key = 0 
    // tree_key = 2*parent(+1/2)
    
    pub fn print_tree(tree: &[Data], tree_key: usize, depth: usize) {
        if 2*tree_key+1 < tree.len() {
            Solution::print_tree(&tree, 2*tree_key+1, depth+1);
        }
    
        for _ in 0..depth {
            print!("    ");
        }
        println!("- {}:{}", tree[tree_key].index, tree[tree_key].value);
    
        if 2*tree_key+2 < tree.len() {
            Solution::print_tree(&tree, 2*tree_key+2, depth+1);
        }
    }
    
    fn find_smallest(tree: &Child, start: usize, end: usize, size: usize) -> (i32, usize) {
        return Solution::find_smallest_inner(tree, start, end, 0, size);
    }
    
    fn find_smallest_inner(tree: &Child, start: usize, end: usize, child_start: usize, child_end: usize) -> (i32, usize) {
        // start, inclusive
        // end: not-inclusive
    
        if start <= child_start && end >= child_end { // TOOD check if it is <= or <
            return (tree.value, tree.index);
        }
    
        if child_start + 1 == child_end {
            return (tree.value, tree.index);
        }
        
        let middle = child_start + (child_end - child_start)/2;
        let mut first: Option<(i32,usize)> = None;
        if start < middle {
            if tree.left.is_none() {
                panic!("empty left {} {}", child_start, middle);
            }
            first = Some(Solution::find_smallest_inner(tree.left.as_ref().unwrap(), start, end, child_start, middle));
        }
        let mut second: Option<(i32,usize)> = None;
        if end > middle {
            if tree.right.is_none() {
                panic!("empty right {} {}", end, middle);
            }
            second = Some(Solution::find_smallest_inner(tree.right.as_ref().unwrap(), start, end, middle, child_end));
        }
    
        return match (first, second) {
            (Some(i), Some(j)) => if i.0 < j.0 { i } else { j },
            (Some(i), None) => i,
            (None, Some(j)) => j,
            (None, None) => (i32::MAX, 0)
        };
    }
    
    fn create_tree(target:&[i32]) -> Vec<Data> {
        let mut tree: Vec<Data> = Vec::with_capacity(target.len());
        tree.resize(5, Data { index: 0, value: 0});
        //return Solution::create_tree_inner(target, 0);
        return tree;
    }
    
    fn create_tree_inner(target:&[i32], mut tree:Vec<Data>, start: usize) {
        let middle = target.len()/2;
    
        if target.len() == 1 {
            return Child {
                left: None,
                right: None,
                value: target[0],
                index: start,
            };
        }
    
        let mut left:Option<Child> =  None;
        if middle > 0 {
            left = Some(Solution::create_tree_inner(&target[..middle], start));
        }
    
        let mut right:Option<Child> =  None;
        if middle < target.len() {
            right = Some(Solution::create_tree_inner(&target[middle..], start+middle));
        }
    
        let value = match (&left, &right) {
            (None,None) => panic!(""), // shouldn't occur 
            (Some(i), Some(j)) => if i.value < j.value { (i.value, i.index) } else { (j.value, j.index) },
            (Some(i), None) => (i.value, i.index),
            (None, Some(i)) => (i.value, i.index)
        };
    
        return Child {
            left: if left.is_none() { None } else { Some(Box::new(left.unwrap())) },
            right: if right.is_none() { None } else { Some(Box::new(right.unwrap())) },
            value: value.0,
            index: value.1
        };
    }
    
    fn compute(tree: &Child, start: usize, end: usize, base:i32, len: usize) -> i32 {
        if start+1 == end {
            let (value, _) = Solution::find_smallest(&tree, start, end, len);
            return value - base;
        }
    
        let (value, index) = Solution::find_smallest(&tree, start, end, len);
    
        let mut step = value - base;
        if start < index { 
            step += Solution::compute(&tree, start, index, value, len);
        }
        if index+1 < end {
            step += Solution::compute(&tree, index+1, end, value, len);
        }
        return step;
    }

    pub fn min_number_operations(input: Vec<i32>) -> i32 {
        let tree = Solution::create_tree(&input);
        Solution::print_tree(&tree, 0, 0);
        //let steps = Solution::compute(&tree, 0, input.len(), 0, input.len());
        let steps = 0;
        return steps;
    }
}

